import React from "react";
import "../css/Icon.css";

function Icon({ isEmoji, src, name, size = "1.5rem" }) {
  const styles = {
    width: size,
    height: size
  }

  if (isEmoji) {
    return (
      <span
      style={styles}
        className="icon-emoji"
        role="img"
        aria-label={name ? name : ""}
        aria-hidden={name ? "false" : "true"}
      >
        {src}
      </span>
    );
  } else {
    return (
      <img
        style={styles}
        className="icon"
        src={"img/" + src}
        alt={name + " icon"}
      ></img>
    );
  }
}

export default Icon;
