import React, { useEffect, useState } from "react";
import "../css/ContentPage.css";
import Icon from "./Icon";
import axios from "axios";
import Task from "./Task";
import { ChromePicker } from "react-color";
import { Popper } from "@mui/material";

function ContentPage({ taskListData }) {
  const [tasks, setTasks] = useState(null);
  const [color, setColor] = useState('#c4eedd');
  const [isMoreMenuOpen, setIsMoreMenuOpen] = useState(false);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/tasks/${taskListData._id}`)
      .then((response) => setTasks(response.data))
      .catch((error) => console.error(error));
  }, [taskListData._id]);

  function handleChange(newColor) {
    setColor(newColor.hex);
  }

  function handleMoreClick(){
    setIsMoreMenuOpen((prev)=>!prev);
  }
  const moreButtonRef = React.useRef(null);

  if (!tasks) {
    return null;
  }
  return (
    <div className="contentpage" style={{backgroundColor:color}}>
      <img
        className="contentpage__burger-icon"
        src="https://upload.wikimedia.org/wikipedia/commons/thumb/b/b2/Hamburger_icon.svg/1200px-Hamburger_icon.svg.png"
        alt="lines icon"
      ></img>
      <div className="contentpage__container">
        <div className="contentpage__header">
          <Icon
            isEmoji={taskListData.img.isEmoji}
            src={taskListData.img.src}
            name={taskListData.name}
          />
          <h3 className="contentpage__title">{taskListData.name}</h3>
          <button className="contentpage__more-button" ref={moreButtonRef} onClick={()=>handleMoreClick()}>⋮</button>
          <Popper
                open={isMoreMenuOpen}
                anchorEl={moreButtonRef.current}
                placement="right-start"
              >
                <ul className="more-button__menu">
                  <p className="more-button__menu-item">Delete list</p>
                  <p className="more-button__menu-item">Change color</p>
                </ul>
              </Popper>
        </div>
        <div className="contentpage__tasks">
          <ul className="tasks">
            {tasks.map((taskData, index) => (
              <Task key={taskData._id} taskData={taskData} />
            ))}
          </ul>
        </div>
      </div>
      <ChromePicker color={color} onChange={handleChange} />
    </div>
  );
}

export default ContentPage;
