import React, { useState } from "react";
import "../css/Grid.css";
import Sidebar from "./Sidebar";
import ContentPage from "./ContentPage";
import taskListsData from "../database";

function Grid() {
  const [taskLists, setTaskLists] = useState(taskListsData);
  const [activeTaskList, setActiveTaskList] = useState(taskLists[0]);
  return (
    <div className="grid">
      <Sidebar
        addTaskList={setTaskLists}
        taskListsData={taskLists}
        activeTaskList={activeTaskList}
        setActiveTaskList={setActiveTaskList}
      />
      <ContentPage taskListData={activeTaskList} />
    </div>
  );
}

export default Grid;
