import React, { useState } from "react";
import "../css/TaskListItem.css";
import Icon from "./Icon";
import axios from "axios";

function TaskListItem({ taskList, changeActiveTaskList, updateTaskListsData }) {
  const [isHovered, setIsHovered] = useState(false);

  function deleteTaskList(){
    if(isHovered){
      axios.delete(`http://localhost:5000/api/taskLists/${taskList._id}`).then(()=>{updateTaskListsData(taskList._id);}).catch((error)=>{console.error("Error deleting task list:", error);})
    }
  }

  return (
    <li
      className="tasklistitem"
      onClick={changeActiveTaskList}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="tasklistitem-container">
        <div className="tasklistitem-title">
          <Icon
            isEmoji={taskList.img.isEmoji}
            src={taskList.img.src}
            name={taskList.name}
          />
          <h3 className="tasklistitem-name">{taskList.name}</h3>
        </div>
        <button
          className={
            isHovered
              ? "tasklistitem__delete-button"
              : "tasklistitem__delete-button --hidden"
          } onClick={de}
        ></button>
      </div>
    </li>
  );
}

export default TaskListItem;
