import React, { useState } from "react";
import data from "@emoji-mart/data";
import Picker from "@emoji-mart/react";
import "../css/TaskListPopUp.css";
import Icon from "./Icon";
import Popper from "@mui/material/Popper";

function TaskListPopUp({ openTaskListPopUp }) {
  const [icon, setIcon] = useState("ðŸ«¥");
  const [isPickerOpen, setIsPickerOpen] = useState(false);
  const [taskList, setTaskList] = useState({ name: "", icon: icon });
  const anchorRef = React.useRef(null);

  function OpenPicker(event) {
    setIsPickerOpen((prev) => !prev);
  }
  function ChooseEmoji(emoji) {
    setIcon(emoji.native);
    setIsPickerOpen((prev) => !prev);
  }
  function handleInputChange(event) {
    const { name, value } = event.target;
    setTaskList((prevTaskList) => ({
      ...prevTaskList,
      [name]: value,
    }));
  }
  function createTaskList() {
    document.querySelector("#taskListNameInput");
  }
  function handleSubmit() {}

  return (
    <div className="tasklist-popup">
      <div className="tasklist-popup__container">
        <h3 className="tasklist-popup__title">Create task list</h3>
        <form className="tasklist-popup__form" onSubmit={() => handleSubmit}>
          <div className="form__item">
            <label className="tasklist-popup__label">Name:</label>
            <input
              id="taskListNameInput"
              type="text"
              name="name"
              value={taskList.name}
              onChange={handleInputChange}
              required
            ></input>
          </div>
          <div className="form__item-inline">
            <label className="tasklist-popup__label">Icon:</label>
            <div
              className="icon-wrapper"
              ref={anchorRef}
              onClick={() => OpenPicker()}
            >
              <Icon isEmoji={true} src={icon} name="New list icon" size="2.5" />
            </div>
            <Popper
              open={isPickerOpen}
              anchorEl={anchorRef.current}
              placement="right-start"
            >
              <Picker data={data} onEmojiSelect={ChooseEmoji} theme="light" />
            </Popper>
          </div>
          <input className="form__item" type="submit"></input>
        </form>
      </div>
    </div>
  );
}
export default TaskListPopUp;
