import React from "react";
import axios from "axios";

function Task({ taskData }) {
  function GetParentList() {
    const parentTaskListName = "";
    axios
      .get(`http://localhost:5000/api/taskLists/${taskData.taskLists[0]}`)
      .then((response) => (parentTaskList = response.data.name))
      .catch((error) => console.error(error));
      return parentTaskListName;
  }

  return (
    <li className="task">
      <div className="task__container">
        <h4 className="task__name">{taskData.name}</h4>
        <h5 className="task__parent-list">{GetParentList()}</h5>
        <p className="task__date">
          {taskData.date.day}.{taskData.date.month}.{taskData.date.year}{" "}
          {taskData.date.hour}:{taskData.date.minute}
        </p>
        <button className="task__important-mark"></button>
      </div>
    </li>
  );
}

export default Task;
